symbol.paint:
painter->setPen(QPen(Qt::red, 0));
painter->setBrush(Qt::red);
QPainterPath path = painterPath();
painter->drawPath(path);

symbol.painterPath:
QRectF rect(-m_w / 2, -m_h / 2, m_w, m_h);
QRectF r = rect.normalized();

if (r.isNull())
  return path;

qreal x = r.x();
qreal y = r.y();
qreal w = r.width();
qreal h = r.height();
qreal wh = w / 2;
qreal hh = h / 2;

path.moveTo(x, y+hh);
path.lineTo(x+wh, y);
path.lineTo(x+w, y+hh);
path.lineTo(x+wh, y+h);
path.closeSubpath();
