symbol.private_block:
qreal m_w;
qreal m_h;
qreal m_r;

symbol.constructor:
m_w = caps[1].toDouble() / 1000.0;
m_h = caps[2].toDouble() / 1000.0;
m_r = caps[3].toDouble() / 1000.0;

symbol.paint:
painter->setPen(QPen(Qt::red, 0));
painter->setBrush(Qt::red);
QPainterPath path = painterPath();
painter->drawPath(path);

symbol.painterPath:
QRectF rect(-m_w / 2, -m_h / 2, m_w, m_h);
QRectF r = rect.normalized();

if (r.isNull())
  return path;

qreal x = r.x();
qreal y = r.y();
qreal w = r.width();
qreal h = r.height();

path.moveTo(x, y+h-m_r);
path.lineTo(x, y+m_r);
path.lineTo(x+m_r, y);
path.lineTo(x+w-m_r, y);
path.lineTo(x+w, y+m_r);
path.lineTo(x+w, y+h-m_r);
path.lineTo(x+w-m_r, y+h);
path.lineTo(x+m_r, y+h);
path.closeSubpath();
