symbol.paint:
painter->setPen(QPen(Qt::red, 0));
painter->setBrush(Qt::red);
QPainterPath path;
addOval(path, QRectF(-m_w / 2, -m_h / 2, m_w, m_h), false);
painter->drawPath(path);

symbol.function_body:
void OvalSymbol::addOval(QPainterPath& path, const QRectF& rect, bool offset)
{
  QRectF r = rect.normalized();
  qreal ox = 0, oy = 0;

  if (r.isNull())
    return;

  if (offset) {
    ox = pos().x();
    oy = pos().y();
  }

  qreal x = ox + r.x();
  qreal y = oy + r.y();
  qreal w = r.width();
  qreal h = r.height();

  qreal rad = h / 2;

  path.arcMoveTo(x, y, h, h, 90);
  path.arcTo(x, y, h, h, 270, -180);
  path.arcTo(x+w-h, y, h, h, 90, -180);
  path.lineTo(x+rad, y+h);
  path.closeSubpath();
}
