symbol.painterPath:
m_cachedPath.moveTo( 0, 0 );
m_cachedPath.lineTo( 0, -m_r );
m_cachedPath.lineTo( -m_r, -m_r );
m_cachedPath.lineTo( -m_r, 0 );
m_cachedPath.lineTo( 0, 0 );
m_cachedPath.lineTo( 0, m_r );
m_cachedPath.lineTo( m_r, m_r );
m_cachedPath.lineTo( m_r, 0 );
m_cachedPath.closeSubpath();
