symbol.painterPath:
QRectF rect(-m_w / 2, -m_h / 2, m_w, m_h);
QRectF r = rect.normalized();

if (r.isNull())
  return m_cachedPath;

qreal x = r.x();
qreal y = r.y();
qreal w = r.width();
qreal h = r.height();
qreal wh = w / 2;
qreal hh = h / 2;

m_cachedPath.moveTo(x, y+hh);
m_cachedPath.lineTo(x+wh, y);
m_cachedPath.lineTo(x+w, y+hh);
m_cachedPath.lineTo(x+wh, y+h);
m_cachedPath.closeSubpath();
