symbol.private_block:
qreal m_w;
qreal m_h;
qreal m_r;

symbol.constructor:
m_w = caps[1].toDouble();
m_h = caps[2].toDouble();
m_r = caps[3].toDouble();

symbol.paint:
painter->setPen(QPen(Qt::red, 0));
painter->setBrush(Qt::red);
QPainterPath path;
addOctagon(path, QRectF(-m_w / 2, -m_h / 2, m_w, m_h), m_r);
painter->drawPath(path);

symbol.function_body:
void OctagonSymbol::addOctagon(QPainterPath& path, const QRectF& rect,
    qreal c)
{
  QRectF r = rect.normalized();

  if (r.isNull())
    return;

  qreal x = r.x();
  qreal y = r.y();
  qreal w = r.width();
  qreal h = r.height();

  path.moveTo(x, y+h-c);
  path.lineTo(x, y+c);
  path.lineTo(x+c, y);
  path.lineTo(x+w-c, y);
  path.lineTo(x+w, y+c);
  path.lineTo(x+w, y+h-c);
  path.lineTo(x+w-c, y+h);
  path.lineTo(x+c, y+h);
  path.closeSubpath();
}
